import { useEffect, useState, useCallback } from 'react';
import { NavLink } from 'react-router-dom';
import BtnDarkMode from '../btnDarkMode/BtnDarkMode';
import './style.css'; // Подключение стилей

const Navbar = () => {
  const [isVisible, setIsVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Функция для управления видимостью навбара при скролле
  const controlNavbar = useCallback(() => {
    if (window.scrollY > lastScrollY) {
      // Прокрутка вниз - скрыть навбар
      setIsVisible(false);
    } else {
      // Прокрутка вверх - показать навбар
      setIsVisible(true);
    }
    setLastScrollY(window.scrollY);
  }, [lastScrollY]);

  useEffect(() => {
    window.addEventListener('scroll', controlNavbar);
    return () => {
      window.removeEventListener('scroll', controlNavbar);
    };
  }, [controlNavbar]);

  // Функция для переключения состояния мобильного меню
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const activeLink = 'nav-list__link nav-list__link--active';
  const normalLink = 'nav-list__link';

  return (
    <nav className={`nav ${isVisible ? 'nav--visible' : 'nav--hidden'}`}>
      <div className="container">
        <div className="nav-row">
          <NavLink to="/" className="logo">
            Донецкие Аэрозоли
          </NavLink>

          {/* Кнопка переключения темной темы */}
          <BtnDarkMode className="btn-dark-mode" />

          {/* Кнопка для открытия мобильного меню */}
          <button className="nav-toggle" onClick={toggleMenu}>
            &#9776;
          </button>

          {/* Список навигации */}
          <ul className={`nav-list ${isMenuOpen ? 'nav-list--open' : ''}`}>
            <li className="nav-list__item">
              <NavLink
                to="/"
                className={({ isActive }) => (isActive ? activeLink : normalLink)}
                onClick={() => setIsMenuOpen(false)} // Закрыть меню при выборе пункта
              >
                Главная
              </NavLink>
            </li>
            <li className="nav-list__item">
              <NavLink
                to="/projects"
                className={({ isActive }) => (isActive ? activeLink : normalLink)}
                onClick={() => setIsMenuOpen(false)}
              >
                Продукция
              </NavLink>
            </li>
            <li className="nav-list__item">
              <NavLink
                to="/contacts"
                className={({ isActive }) => (isActive ? activeLink : normalLink)}
                onClick={() => setIsMenuOpen(false)}
              >
                Контакты
              </NavLink>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
