import { useState } from 'react';
import { projects } from '../../helpers/projectsList';
import './style.css';

const Header = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleNextSlide = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === projects.length - 1 ? 0 : prevSlide + 1
    );
  };

  const handlePrevSlide = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === 0 ? projects.length - 1 : prevSlide - 1
    );
  };

  return (
    <header className="header">
      <div className="carousel">
        <div className="carousel-container">
          <div
            className="carousel-slide"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {projects.map((project, index) => (
              <div key={index} className="carousel-item">
                <img
                  src={project.img}
                  alt={project.title}
                  className="carousel-item__img"
                />
                <div className="carousel-item__info">
                  <h3 className="carousel-item__title">{project.title}</h3>
                  <p className="carousel-item__description">
                    {project.description}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>

        <button className="carousel-prev" onClick={handlePrevSlide}>
          <span className="carousel-prev__icon">&lt;</span>
        </button>
        <button className="carousel-next" onClick={handleNextSlide}>
          <span className="carousel-next__icon">&gt;</span>
        </button>
      </div>
    </header>
  );
};

export default Header;
