import { useParams } from "react-router-dom";
import { projects } from "./../helpers/projectsList";
import './Project.css';  // Подключаем стили

const Project = () => {
    const { id } = useParams();
    const project = projects[id];

    return (
        <main className="section">
            <div className="container">
                <h1 className="title-1">{project.title}</h1>
                <div className="project-details">
                    <img
                        src={project.imgBig}
                        alt={project.title}
                        className="project-details__cover"
                    />

                    <div className="project-details__desc">
                        <p>{project.description}</p>

                        {/* Перечисляемый список особенностей */}
                        <section className="project-features">
                            <h2 className="section-title">Особенности</h2>
                            <ul className="feature-list">
                                {project.features.map((feature, index) => (
                                    <li key={index}>{feature}</li>
                                ))}
                            </ul>
                        </section>

                        {/* Ассортимент */}
                        <section className="project-assortment">
                            <h2 className="section-title">Ассортимент</h2>
                            <ul className="assortment-list">
                                {project.assortment.map((item, index) => (
                                    <li key={index} className="assortment-item">
                                        {item}
                                    </li>
                                ))}
                            </ul>
                        </section>

                        {/* Таблица характеристик */}
                        <section className="project-specs">
                            <h2 className="specs-title">Характеристики</h2>
                            <table className="specs-table">
                                <tbody>
                                    {project.specs.map((spec, index) => (
                                        <tr key={index}>
                                            <td>{spec.name}</td>
                                            <td>{spec.value1}</td>
                                            {spec.value2 && <td>{spec.value2}</td>}
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </section>

                        <div className="button-container">
                            <a
                                href="https://web.telegram.org/k/#@polkiqwase"
                                target="_blank"
                                rel="noreferrer"
                                className="btn-outline-3"
                            >
                                заказать
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    );
};

export default Project;
