import { useParams } from "react-router-dom";
import { projects } from "./../helpers/projectsList";
import './Project.css';  // Подключаем стили

const Project = () => {
    const { id } = useParams();
    const project = projects[id];

    return (
        <main className="section">
            <div className="container">
                <h1 className="title-1">{project.title}</h1>
                <div className="project-details">
                    <img
                        src={project.imgBig}
                        alt={project.title}
                        className="project-details__cover"
                    />

                    <div className="project-details__desc">
                        <p>{project.description}</p>

                        {/* Перечисляемый список особенностей */}
                        <ul className="project-features">
                            {project.features.map((feature, index) => (
                                <li key={index}>{feature}</li>
                            ))}
                        </ul>

                        {/* Ассортимент */}
                        <p className="project-assortment">{project.assortment}</p>

                        {/* Таблица характеристик */}
                        <table className="project-specs">
                            <tbody>
                                {project.specs.map((spec, index) => (
                                    <tr key={index}>
                                        <td>{spec.name}</td>
                                        <td>{spec.value}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>

                        <div className="button-container">
                            <a
                                href="https://web.telegram.org/k/#@polkiqwase"
                                target="_blank"
                                rel="noreferrer"
                                className="btn-outline-3"
                            >
                                заказать
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    );
};

export default Project;
